mapper.sql:
WHERE fb.created_at BETWEEN #{startDateTimestamp} AND #{endDateTimestamp}
AND (fb.title LIKE CONCAT('%',#{searchText},'%') OR fb.content LIKE CONCAT('%',#{searchText},'%') OR a.admin_name LIKE CONCAT('%',#{searchText},'%') OR m.member_name LIKE CONCAT('%',#{searchText},'%'))
AND fb.category_id = #{category}
