mapper.sql:
WHERE gb.created_at BETWEEN #{startDateTimestamp} AND #{endDateTimestamp}
AND (gb.title LIKE CONCAT('%',#{searchText},'%') OR gb.content LIKE CONCAT('%',#{searchText},'%') OR a.admin_name LIKE CONCAT('%',#{searchText},'%') OR m.member_name LIKE CONCAT('%',#{searchText},'%'))
AND gb.category_id = #{categoryId}
